WITH alert_event AS (SELECT *, action AS derived_action, policy AS derived_policy, transaction_id AS derived_transaction_id FROM (SELECT A.*, SPLIT (A.organization_unit, '/') AS OU, CONCAT (A.app, A.instance_id) AS ns_app_instance FROM "redshift_poc_iceberg"."alert_v3" AS A WHERE A.ns_tenant_id=2683 AND ( ( (A.TIMESTAMP) >= (TIMESTAMP '2025-01-03') AND (A.TIMESTAMP) < (TIMESTAMP '2025-01-17') ) ) ) ) SELECT COUNT (DISTINCT alert_event.derived_policy) AS "policies" FROM alert_event LEFT JOIN "redshift_poc_iceberg"."usergroups" AS groups ON alert_event.ns_tenant_id = groups.ns_tenant_id AND (LOWER (alert_event.user) ) = (LOWER (groups.group_user) ) CROSS JOIN UNNEST (groups.groupnames) AS T (usergroups) WHERE ( ( (alert_event.TIMESTAMP) >= (TIMESTAMP '2025-01-03') AND (alert_event.TIMESTAMP) < (TIMESTAMP '2025-01-17') ) ) AND ( (UPPER ( (usergroups) ) = UPPER ('web') ) ) AND (alert_event.ns_tenant_id) = 2683 AND (1=1 AND 1=1 AND 1=1 AND 1=1 AND 1=1 AND 1=1 AND 1 = 1) LIMIT 500